mapper.update:
UPDATE MEMBER 
SET MEMBER_STATUS = #{memberStatus}, MEMBER_BAN_START = #{banStart}, MEMBER_BAN_END = #{banEnd}
WHERE MEMID = #{memId}

mapper.select:
SELECT * FROM ( SELECT m.MEMID, m.MEMNICK, m.MEMEMAIL, m.MEMREGDATE, m.MEMUPDATE, m.MEMBER_BAN_START AS memberbanstart, m.MEMBER_BAN_END AS memberbanend, c1.CODENAME AS MEMBER_STATUS, c2.CODENAME AS MEMBER_MBTI, c3.CODENAME AS MEMBER_TT, ROW_NUMBER() OVER (ORDER BY m.MEMID) AS rn FROM MEMBER m LEFT JOIN CODE c1 ON m.MEMBER_STATUS = c1.CODEID LEFT JOIN CODE c2 ON m.MEMBER_MBTI = c2.CODEID LEFT JOIN CODE c3 ON m.MEMBER_TT = c3.CODEID
m.MEMNICK LIKE '%' || #{dto.search} || '%'
AND MEMBER_STATUS = #{dto.activateFlag}
) WHERE rn BETWEEN #{offset} + 1 AND #{offset} + #{pageRowCount}

SELECT COUNT(*) FROM MEMBER m LEFT JOIN CODE c1 ON m.MEMBER_STATUS = c1.CODEID LEFT JOIN CODE c2 ON m.MEMBER_MBTI = c2.CODEID LEFT JOIN CODE c3 ON m.MEMBER_TT = c3.CODEID
m.MEMNICK LIKE '%' || #{dto.search} || '%'
AND c1.CODEID = #{dto.activateFlag}
