mapper.select:
SELECT *
FROM (
    SELECT qnaid, memid, memnick, qna_001, qna_002, commentstatus, qnatitle, qnatext, qnaregdate, qnaupdate,
           ROW_NUMBER() OVER(ORDER BY qnaregdate DESC) AS rn
    FROM qna



                AND qna_001 = #{dto.searchType}



)
WHERE rn BETWEEN #{offset} + 1 AND #{offset} + #{pageRowCount}

SELECT COUNT(*)
FROM qna



            AND qna_001 = #{dto.searchType}

SELECT * FROM ( SELECT qnaid, memid, memnick, qna_001, qna_002, commentstatus, qnatitle, qnatext, qnaregdate, qnaupdate, ROW_NUMBER() OVER(ORDER BY qnaregdate DESC) AS rn FROM qna WHERE memid = #{memId}
AND (qnatitle LIKE '%' || #{dto.search} || '%' OR qnatext LIKE '%' || #{dto.search} || '%')
) WHERE rn BETWEEN (#{dto.page} - 1) * 8 + 1 AND #{dto.page} * 8

select memNick
from member
where memid = #{memid}

SELECT QNACID, QNAID, QNACTEXT, QNACREGDATE, QNACUPDATE 
FROM QNACOMMENT 
WHERE QNAID = #{qnaId}
